function loadHelpers() {
    let helpers = {};
    helpers.setUpRateBandData = function setUpRateBand(golden_id, partner_golden_id, name) {
        var rateBand = {};
        rateBand.golden_id = golden_id;
        rateBand.partner_golden_id = partner_golden_id;
        rateBand.name  = name;
        pm.globals.set("rate-band", JSON.stringify(rateBand));
    };
    helpers.setUpPartnerData = function setUpPartner(golden_id, status, currency, cease_date) {
        var partner = {};
        partner.golden_id = golden_id;
        partner.status  = status;
        partner.currency  = currency;
        pm.globals.set("partner", JSON.stringify(partner));
    };
    helpers.setUpExperienceData = function setUpExperience(golden_id, partner_golden_id, name, description, duration) {
        var experience = {};
        experience.golden_id = golden_id;
        experience.partner_golden_id = partner_golden_id;
        experience.name = name;
        experience.description = description;
        experience.duration  = duration;
        pm.globals.set("experience", JSON.stringify(experience));
    }
    helpers.setUpBoxData = function setUpBox(golden_id, brand, country, status) {
        var box = {};
        box.golden_id = golden_id;
        box.brand  = brand;
        box.country  = country;
        box.status  = status;
        pm.globals.set("box", JSON.stringify(box));
    }
    helpers.setUpRoomData = function setUpRoom(goldenId, partnerGoldenId, name, description, inventory, isSellable, status) {
        var room = {};
        room.golden_id = goldenId;
        room.partner_golden_id = partnerGoldenId;
        room.name = name;
        room.description = description;
        room.inventory = inventory;
        room.is_sellable = isSellable;
        room.status = status;
        pm.globals.set("room", JSON.stringify(room));
    }
    return helpers;
}
loadHelpers();
